INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253698, 'Pitolisant', 'Lumacaftor', 'Major', 'Coadministration with potent CYP450 3A4 inducers may decrease the systemic exposure and efficacy of pitolisant.  The proposed mechanism is induction of CYP450 3A4-mediated metabolism of pitolisant, which has been shown to be metabolized by this isoenzyme.', 'DDInter', 'Patients should be assessed for loss of efficacy of pitolisant following initiation of a potent CYP450 3A4 inducer, and the pitolisant dosage adjusted as necessary.  For patients already receiving a stable dose of pitolisant 8.9 mg or 17.8 mg once daily, the dose of pitolisant should be increased to double the original daily dose (i.e., 17.8 mg or 35.6 mg, respectively) over 7 days.  The dosage of pitolisant should be reduced by half if concomitant use of a strong CYP450 3A4 inducer is discontinued.', 'Metabolism', 'Patients should be assessed for loss of efficacy of pitolisant following initiation of a potent CYP450 3A4 inducer, and the pitolisant dosage adjusted as necessary.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301306/', '', 'Dextromethorphan, Dalfampridine, Sodium oxybate, Riluzole, Tetrabenazine, Inotersen, Amifampridine, Deutetrabenazine, Tafamidis', 'Nitric Oxide, Doxapram, Ivacaftor, Elexacaftor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253699, 'Polatuzumab vedotin', 'Lumacaftor', 'Minor', 'Coadministration with potent inducers of CYP450 3A4 may decrease exposure to unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of polatuzumab vedotin.  Polatuzumab vedotin is an antibody-drug conjugate (ADC) that releases MMAE via proteolytic cleavage, and MMAE has been shown in vitro to be primarily metabolized by CYP450 3A4.', 'DDInter', 'The clinical relevance is unknown', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301307/', '', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 'Nitric Oxide, Doxapram, Elexacaftor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253700, 'Ponatinib', 'Lumacaftor', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of ponatinib, which is a substrate of the isoenzyme.', 'DDInter', 'Coadministration of ponatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided unless the benefit outweighs the potential risk of ponatinib underexposure.  Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible.  If concomitant use is required, close monitoring for signs of reduced therapeutic efficacy is advised.  Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with ponatinib is unknown.', 'Metabolism', 'Coadministration of ponatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301309/', '', 'Futibatinib, Lenvatinib, Trametinib, Afatinib, Dacomitinib, Pazopanib, Alectinib', 'Nitric Oxide, Doxapram, Ivacaftor, Elexacaftor', 1767369485);
