INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253821, 'Dalfampridine', 'Tipiracil', 'Moderate', 'Coadministration with inhibitors and/or substrates of organic cation transporter 2 (OCT2) may increase the plasma concentrations of dalfampridine.  In vitro studies have shown that OCT2 is the primary transporter responsible for the active tubular secretion of dalfampridine, which accounts for approximately 60% of its renal elimination--the major route of clearance for dalfampridine.', 'DDInter', 'Caution is advised when dalfampridine is used with OCT2 inhibitors and/or substrates.  The potential benefits of concomitant use should be considered against the risk of seizures.', 'Excretion', 'Caution is advised when dalfampridine is used with OCT2 inhibitors and/or substrates.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301470/', '', 'Dextromethorphan, Valbenazine, Sodium oxybate, Riluzole, Tetrabenazine, Inotersen, Pitolisant, Amifampridine, Deutetrabenazine, Tafamidis', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253822, 'Deferiprone', 'Trifluridine', 'Major', 'Coadministration of deferiprone and other drugs that can cause neutropenia or agranulocytosis may increase the risk and/or severity of hematologic toxicity.', 'DDInter', 'Concomitant use of deferiprone with other drugs known to be associated with neutropenia or agranulocytosis should generally be avoided.  Otherwise, close monitoring of absolute neutrophil count (ANC) is recommended if coadministration is required.  The ANC should be measured prior to initiating deferiprone and weekly during therapy.  The manufacturer recommends that treatment be interrupted if neutropenia (ANC between 0.5 and 1.5 x 10^9/L) or infection develops.  Patients should be advised to immediately discontinue deferiprone as well as all other medications with a potential to cause neutropenia and seek medical attention if they experience symptoms indicative of infection such as fever, sore throat, and flu-like symptoms.  A complete blood cell (CBC) count, including a white blood cell (WBC) count corrected for the presence of nucleated red blood cells, an absolute neutrophil count (ANC), and a platelet count should be obtained daily until recovery.  For patients who develop agranulocytosis (ANC less than 0.5 x 10^9/L), hospitalization should be considered, and deferiprone should not be resumed following recovery unless potential benefits outweigh the risks.  Likewise, patients who develop neutropenia with deferiprone should not be rechallenged unless potential benefits outweigh the risks.', 'Synergism', 'Concomitant use of deferiprone with other drugs known to be associated with neutropenia or agranulocytosis should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301471/', '', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Gatifloxacin, Rifamycin, Kanamycin, Lomefloxacin, Ampicillin, Azithromycin, Ofloxacin, Acyclovir, Netilmicin, Ciprofloxacin, Polymyxin B, More', 1767369485);
