INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253862, 'Trifluridine', 'Ocrelizumab', 'Moderate', 'The use of the CD20-directed cytolytic antibody ocrelizumab with other immune-modulating or immunosuppressive therapy, including immunosuppressant doses of corticosteroids may increase the risk of infections.  Ocrelizumab alone has been reported to increase the risk for upper and lower respiratory tract infections, skin infections, and herpes-related infections.', 'DDInter', 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy.  In addition, the prolonged immunosuppressant effects and mode of action of other immunosuppressant drugs such as daclizumab, fingolimod, natalizumab, teriflunomide, or mitoxantrone should be considered prior to starting ocrelizumab therapy.  Patients should be advised to notify their doctor if they develop signs or symptoms of infection, including upper or lower respiratory tract infection, skin infection, herpes related infection, or PML.', 'Synergism', 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301530/', '', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, More', 'Gatifloxacin, Rifamycin, Kanamycin, Lomefloxacin, Ampicillin, Azithromycin, Ofloxacin, Acyclovir, Netilmicin, Ciprofloxacin, Polymyxin B, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253863, 'Trifluridine', 'Olaparib', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with olaparib.', 'DDInter', 'Caution is advised if olaparib is prescribed with other myelosuppressive agents.  Complete blood counts should be performed at baseline and monthly thereafter.  Do not start olaparib until patients have recovered from hematologic toxicity caused by previous chemotherapy.  For prolonged hematologic toxicities, olaparib should be interrupted and blood counts monitored weekly until recovery.  If the levels have not recovered to CTCAE Grade 1 or less after 4 weeks, a haematologist should be consulted for further investigations, including bone marrow analysis and blood sample for cytogenetics.  If MDS/AML is confirmed, discontinue olaparib.  Patients should be advised to contact their physician if they experience weakness, fatigue, fever, weight loss, infections, shortness of breath, unusual bleeding or bruising, or blood in urine or stool.', 'Synergism', 'Caution is advised if olaparib is prescribed with other myelosuppressive agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301531/', '', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Gatifloxacin, Rifamycin, Kanamycin, Lomefloxacin, Ampicillin, Azithromycin, Ofloxacin, Acyclovir, Netilmicin, Polymyxin B, Famciclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253864, 'Tipiracil', 'Olaparib', 'Moderate', 'Based on in vitro inhibition data, coadministration with olaparib may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 (e.g., cisapride, ergot alkaloids, fentanyl, lovastatin, oral midazolam, pimozide, quetiapine, simvastatin, triazolam, vinca alkaloids), P-gp (e.g., colchicine, dabigatran, digoxin), breast cancer resistance protein (BCRP) (e.g., rosuvastatin), OATP1B1 (e.g., eluxadoline, glyburide, repaglinide, statins, valsartan), or organic cation transporter 1 or 2 (OCT1, OCT2) (e.g., metformin).  The proposed mechanism is decreased clearance due to inhibition of the corresponding metabolizing enzyme and/or efflux/uptake transporter by olaparib.', 'DDInter', 'Caution is advised if olaparib must be used concomitantly with drugs that are substrates of the affected enzymes or transporters, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever olaparib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if olaparib must be used concomitantly with drugs that are substrates of the affected enzymes or transporters, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301532/', '', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', '', 1767369485);
