INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253901, 'Vitamin E', 'Trifluridine', 'Moderate', 'The potential effects of vitamin E on cancer chemotherapy and radiation have not been established.  Because vitamin E is an antioxidant, pharmacologic doses could theoretically interfere with radiation therapy and chemotherapeutic agents whose cytotoxic mechanism depends on generation of reactive oxygen species (ROS) that damage DNA and proteins, including but not limited to alkylating agents (e.g., busulfan, cyclophosphamide, melphalan), anthracyclines (e.g., doxorubicin, epirubicin), platinum coordination complexes (e.g., cisplatin, carboplatin), DNA topoisomerase inhibitors (e.g., etoposide, teniposide, irinotecan, topotecan), and photodynamic agents (e.g., porfimer).  On the other hand, vitamin E may help reduce oxidative stress associated with more aggressive cancers and protect non-cancer cells from oxidative damage generated by cancer treatments, which can enhance patient tolerance and lessen the need for reducing dosage or duration of treatment.', 'DDInter', 'Until more information is available, vitamin E supplementation should preferably be avoided in patients undergoing cancer chemotherapy or radiation treatment.  Clinicians should closely monitor antitumor response if vitamin E is used in these patients.  No specific dosing of vitamin E has been established for uses other than to treat vitamin E deficiency.  There has been evidence suggesting possible adverse health effects including increased risk of heart failure and mortality in association with long-term use of 400 IU/day or greater.', 'Others', 'Until more information is available, vitamin E supplementation should preferably be avoided in patients undergoing cancer chemotherapy or radiation treatment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301607/', '', 'Gatifloxacin, Rifamycin, Kanamycin, Lomefloxacin, Ampicillin, Azithromycin, Ofloxacin, Acyclovir, Netilmicin, Ciprofloxacin, Polymyxin B, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253902, 'Yellow Fever Vaccine', 'Trifluridine', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', 'DDInter', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301609/', '', 'Gatifloxacin, Rifamycin, Kanamycin, Lomefloxacin, Ampicillin, Azithromycin, Ofloxacin, Acyclovir, Netilmicin, Ciprofloxacin, Polymyxin B, More', '', 1767369485);
