INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253988, 'Indinavir', 'Voxilaprevir', 'Moderate', 'Coadministration with inhibitors of organic anion transporting polypeptides (OATP) 1B1 and/or 1B3 may increase the plasma concentrations of voxilaprevir, which is a substrate of the hepatic uptake transporters.', 'DDInter', 'Caution and monitoring are advised when voxilaprevir is used with OATP 1B1 or 1B3 inhibitors.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301827/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Rilpivirine, Etravirine, Dolutegravir, More', 'Glecaprevir, Cabotegravir, Zanamivir, Lenacapavir, Valaciclovir, Telaprevir, Velpatasvir, Rilpivirine, Oseltamivir, Remdesivir, Famciclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253989, 'Insulin human', 'Voxilaprevir', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', 'DDInter', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301828/', '', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Rilpivirine, Etravirine, Dolutegravir, More', '', 1767369485);
